  console.log("== insert documents")
  col.insert(docs, function(e, r) {
    docs = [];
    assert.equal(null, e);

    console.log("== start bench")
    for(var i = 0; i < total; i++) {
      execute(col, function(e) {
        count = count - 1;

        if(count == 0) {
          // Calculate total execution time for operations
